(62, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-02-19 11:36:13', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'amazon_pay_reactivate_q1-24', 'marketing', 'en_US', 'Special offer: Activate 0% promo rate for Amazon Pay', 'For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in the United Kingdom. Terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2024-02-19 11:36:13', NULL, 0, 'plain', '', 0, 0, 'info'),
